mapper.select:
select count(*) from bmember 
	
		where id like #{keyword}
	
	
	
		where name like #{keyword} 
	
	
	
		where gender like #{keyword} 
	
order by num desc

select * from bmember 
	
		
			where id like #{keyword} 
		
		
			where name like #{keyword} 
		
		
			where gender like #{keyword} 
		
		
			where id like #{keyword} or name like #{keyword} or gender like #{keyword} 
		
	
order by num desc
